function codeMsg(){var e=document.getElementById("userInput").value,t=e.replace(/e/gi,"enter").replace(/i/gi,"imes").replace(/a/gi,"ai").replace(/o/gi,"ober").replace(/u/gi,"ufat");document.getElementById("outputCode").value=t,console.log(t),document.getElementById("userInput").value=e}function decodeMsg(){var e=document.getElementById("userInput").value,t=e.replace(/ai/gi,"a").replace(/enter/gi,"e").replace(/imes/gi,"i").replace(/ober/gi,"o").replace(/ufat/gi,"u");document.getElementById("outputCode").value=t,console.log(t),document.getElementById("userInput").value=e}function copyToClipBoard(){var e=document.getElementById("outputCode");e.select(),document.execCommand("copy"),alert("copied")}function eraseText(){document.getElementById("outputCode").value=""}var codeButton=document.querySelector("#codeButton");codeButton.addEventListener("click",function(e){e.preventDefault(),codeMsg()});var decodeButton=document.querySelector("#decodeButton");decodeButton.addEventListener("click",function(e){e.preventDefault(),decodeMsg()});